mapper.select:
SELECT T.* , ROW_NUMBER() OVER(ORDER BY ${ORDER} ${ORDER_RN}, T.GALLERY_SEQ ASC) AS RN FROM (SELECT GL.GALLERY_SEQ , GL.AUTHOR , GL.TYPE , GL.DEL_YN , GL.REG_DATE , GL.TAG , GL.LANG , GL.VIEW_COUNT , GL.LIKE_CNT , GD.FILE_PATH , GD.FILE_NAME , (SELECT COUNT(*) FROM GALLERY_DETAIL S1 WHERE S1.GALLERY_SEQ = GL.GALLERY_SEQ ) CNT FROM GALLERY GL , GALLERY_DETAIL GD WHERE GL.DEL_YN = 'N' AND GL.GALLERY_SEQ = GD.GALLERY_SEQ AND GD.PAGE_SEQ = 1
AND ( GL.AUTHOR LIKE '%${SEARCH_KEYWORD}%' )
) T ORDER BY ${ORDER} ${ORDER_FLAG}, T.GALLERY_SEQ DESC LIMIT ${PAGE_NUM},${PAGE_SIZE}

SELECT COUNT(*) FROM( SELECT GL.GALLERY_SEQ , GL.AUTHOR , GL.TYPE , GL.DEL_YN , GL.REG_DATE , GL.TAG , GL.LANG , GL.VIEW_COUNT , GL.LIKE_CNT , GD.FILE_PATH , GD.FILE_NAME FROM GALLERY GL , GALLERY_DETAIL GD WHERE GL.DEL_YN = 'N' AND GL.GALLERY_SEQ = GD.GALLERY_SEQ AND GD.PAGE_SEQ = 1
AND ( GL.AUTHOR LIKE '%${SEARCH_KEYWORD}%' )
) T

mapper.insert:
/* 갤러리 조회 */
INSERT
	INTO GALLERY
       (
              GALLERY_SEQ,
              AUTHOR,
              TYPE,
              DEL_YN,
              REG_DATE,
              TAG,
              LANG,
              VIEW_COUNT,
              LIKE_CNT
       )
       VALUES
       (	    #{GALLERY_SEQ}
              , #{AUTHOR}
              , 0
              , 'N'
              , #{REG_DATE}
              , #{TAG}
              , #{LANG}
              , 0
              , 0
       )

/* 갤러리 상세 조회 */
INSERT
	INTO GALLERY_DETAIL
       (
              GALLERY_SEQ,
              PAGE_SEQ,
              FILE_PATH,
              FILE_NAME,
              FILE_ETC
       )
       VALUES
       (        #{GALLERY_SEQ}
              , #{PAGE_SEQ}
              , #{FILE_PATH}
              , #{FILE_NAME}
              , #{FILE_ETC}
       )
